package my.basics.program;

public class PrimitiveDataType {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//byte size = 1 byte
				byte bytemax = 127;
				byte bytemin = -128;
				
				System.out.println("max value of byte is = "+bytemax);
				System.out.println("min value of byte is = "+bytemin);
				
				//short size = 2 byte;
				short shortMax = 32767;
				short shortMin = -32768;
				
				System.out.println("max value of short is = "+shortMax);
				System.out.println("min value of short is = "+shortMin);
				
				//int size = 4 byte;
				
				int maxInt= 2147483647;
				int minInt= -2147483648 ;
				
				System.out.println("max value of int is = "+maxInt);
				System.out.println("min value of int is = "+minInt);
				
				//long Size = 8 byte;
				
				long maxLong =9223372036854775807L;
				long minLong =-9223372036854775808L;
				
				System.out.println("max value of long is = "+maxLong);
				System.out.println("min value of long is = "+minLong);
				
				
				float f=3234.141243278345f;
				double d = 3456.14124512345678902345;
				
				System.out.println("float value is = "+f+ "double value is = "+d);
				
				
				//boolean
				
				boolean  flag =false; //flag = true;
				
				System.out.println("boolean value is = "+flag);
				
				
				
				

	}

}
